jQuery(function () {
    var msie6 = jQuery.browser == 'msie' && jQuery.browser.version < 7;
    if (!msie6 && jQuery('#sharebtnsingle').length != 0) {
        var top = jQuery('#sharebtnsingle').offset().top - parseFloat(jQuery('#sharebtnsingle').css('margin-top').replace(/auto/, 0));
	       
        jQuery(window).scroll(function (event) {
            var y = jQuery(this).scrollTop();
            if (y >= top-20) {
                jQuery('#sharebtnsingle').addClass('fixed');
            } else {
                jQuery('#sharebtnsingle').removeClass('fixed');
            }
        });
    }
});
$('a[href=#wrap]').click(function(){$('html, body').animate({scrollTop:0}, 'slow'); return false;});
$('a[href=#leave]').click(function(){$('html, body').animate({scrollTop: $("#leave").offset().top}, 1000); return false;});
